import React, { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import { Context } from '..';

import '../styles/Detail.css';

const DetailMovie = () => {
  const { apiKey } = useContext(Context);
  const history = useHistory();
  const {
    poster, overview, vote, date, title, id
  } = history.location.state;

  const getSearched = async () => {
    const urlSearch = `https://api.themoviedb.org/3/movie/${id}/videos?api_key=<<api_key>>&language=en-US`;
    const response_Search = await fetch(urlSearch);
    const responseJson_Search = await response_Search.json();
    if (responseJson_Search?.results) setMovies(responseJson_Search.results);
  };

  return (
    <div className='detailWrapper'>
      <div className="info">
        <img className="detailPoster" src={poster} alt='' />
        <div>
          <p style={{ fontSize: '30px', marginBottom: '100px' }}>{title}</p>
          <p style={{ fontSize: '30px' }}>{date}</p>
          <p>{overview}</p>
          <span style={{ fontSize: '30px' }}>{vote} &#9734;</span>
          <div style={{ fontSize: '30px', marginTop: '100px', cursor: 'pointer' }}>
            <p>Add to favorites</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetailMovie;
